import React, { useState, useEffect } from 'react';
import Menu from './Menu';
import Post from './Post';
import ListaUser from './ListaPosts'
function Posts() {
    const [listaUsuarios, setListaUsuarios] = useState([]);
    const agregarUsuarios = (post) => {
        setListaUsuarios(listaUsuarios.concat(post));
    }

    const cargandoUsuariosWeb = async () => {
        console.log('Metodo cargando Usuario');
        let datos = await fetch('https://jsonplaceholder.typicode.com/comments')
            .then(response => response.json());
        setListaUsuarios(datos);
    }

    useEffect(() => {
        cargandoUsuariosWeb();
    }, []);
    return <div>
        <Menu />
        <div className="row">
            <div className="col-lg-4">
                <Post agregarUsuarios={agregarUsuarios} />
            </div>
            <div className="col-lg-8">
                <ListaUser listaUsuarios={listaUsuarios} />
            </div>
        </div>
    </div>
}
export default Posts;
