import React, { useState } from 'react';
function Post(props) {
    const [post, setUser] = useState({
        postId: 0,
        id: 0,
        name: "",
        email: "",
        body: ""
    })

    const cambiarValor = (e) => {
        const { name, value, type } = e.target;
        setUser({ ...post, [name]: (type === 'number') ? parseInt(value) : value });
        //limpiar();
    }

    const limpiar = () => {
        setUser({
            postId: 0,
            id: 0,
            name: "",
            email: "",
            body: ""
        })
    }
    return <div>
        <div className="card">
            <div className="card-header">Datos del Post</div>
            <div className="card-body">
            <label for="exampleInputEmail1">Email address</label>

                <input type="number"
                    name="postId"
                    id="postId"
                    placeholder="Digite el Post Id"
                    onChange={cambiarValor}
                    value={post.postId} />
                <input
                    type="number"
                    name="id"
                    id="id"
                    placeholder="Digite el Id"
                    onChange={cambiarValor}
                    value={post.id} />
                <input
                    type="text"
                    name="name"
                    id="name"
                    placeholder="Digite el name"
                    onChange={cambiarValor}
                    value={post.name} />
                <input
                    type="text"
                    name="email"
                    id="email"
                    placeholder="Digite el email"
                    onChange={cambiarValor}
                    value={post.email} />
                <input type="text"
                    name="body"
                    id="body"
                    placeholder="Digite el body"
                    onChange={cambiarValor}
                    value={post.body} />
            </div>
            <div className="card-footer">
                <button
                    className="btn btn-primary"
                    onClick={() => { props.agregarUsuarios(post) }}
                >Guardar</button>
                <button
                    className="btn btn-default"
                    onClick={limpiar}
                >Cancelar</button>
            </div>
        </div>
    </div>
}
export default Post;
