import React, { useState } from 'react';
function User(props) {
    const [user, setUser] = useState({
        postId: 0,
        id: 0,
        name: "",
        email: "",
        body: ""
    })

    const cambiarValor = (e) => {
        const { name, value, type } = e.target;
        setUser({ ...user, [name]: (type === 'number') ? parseInt(value) : value });
        //limpiar();
    }

    const limpiar = () => {
        setUser({
            userId: 0,
            id: 0,
            title: "",
            body: ""
        })
    }
    return <div>
        <div className="card">
            <div className="card-header">Datos del Post</div>
            <div className="card-body">


                <input type="number"
                    name="postId"
                    id="postId"
                    placeholder="Digite el Post Id"
                    onChange={cambiarValor}
                    value={user.postId} />
                <input
                    type="number"
                    name="id"
                    id="id"
                    placeholder="Digite el Id"
                    onChange={cambiarValor}
                    value={user.id} />
                <input
                    type="text"
                    name="name"
                    id="name"
                    placeholder="Digite el name"
                    onChange={cambiarValor}
                    value={user.name} />
                <input
                    type="text"
                    name="email"
                    id="email"
                    placeholder="Digite el email"
                    onChange={cambiarValor}
                    value={user.email} />
                <input type="text"
                    name="body"
                    id="body"
                    placeholder="Digite el body"
                    onChange={cambiarValor}
                    value={user.body} />
            </div>
            <div className="card-footer">
                <button
                    className="btn btn-primary"
                    onClick={() => { props.agregarUsuarios(user) }}
                >Guardar</button>
                <button
                    className="btn btn-default"
                    onClick={limpiar}
                >Cancelar</button>
            </div>
        </div>
    </div>
}
export default User;
